import React, { useState } from 'react';

import './App.css';
import UserInput from "./components/UserInput";
import UserOutput from "./components/UserOutput";



function App(props) {
  const [state, setState] = useState({
    persons: [
      { userName: "Max" },
      { userName: "Tony" },
      { userName: "Barbonie" },
    ],
    showPerson: false,
  });

  const onChangeHanlder = (e) => {
    const temp = state.showPerson;
    setState({
      persons: [
        { userName: "Max" },
        { userName: e.target.value },
        { userName: "Barbonie" },
      ],
      showPerson: temp,
    })
    console.log("state", state.persons)
  };

  const style = {
    backgroundColor: 'gray',
    border: '1px solid black',
    padding: '8px',
    cursor: 'pointer',
  };
  const deleteHandler = (personIndex) => {
    const persons = state.persons;
    persons.splice(personIndex, 1);
    setState({persons:persons})
  }

  const toggleHandler = () => {
    const doesShow = state.showPerson;
    const persons = [...state.persons];
    setState(
      {
        persons: persons,
        showPerson: !doesShow
      });
    console.log("toggle", state.showPerson)
  };

  let people = null;
  if (state.showPerson === false) {
