import React, { useState } from 'react';

import './App.css';
import UserInput from "./components/UserInput";
import UserOutput from "./components/UserOutput";



function App(props) {
  const [userNameState, setUserNameState] = useState({
    usernames: [
      { username: "Max" },
      { username: "Tony" },
      { username: "Barbonie" },
    ],
    showPerson: false,


  });
  console.log("state", userNameState)
  const onChangeHanlder = (e) => {
    const temp = userNameState.showPerson;
    setUserNameState({
      usernames: [
        { username: "Max" },
        { username: e.target.value },
        { username: "Barbonie" },
      ],
      showPerson: temp,
    })

  };
  const style = {
    backgroundColor: 'gray',
    border: '1px solid black',
    padding: '8px',
    cursor: 'pointer',
  };

  const toggleHandler = () => {
    const doesShow = userNameState.showPerson;
    const usernames = [...userNameState.usernames];
    setUserNameState(
      {
        usernames: usernames,
        showPerson: !doesShow
      });
    console.log("toggle", userNameState.showPerson)
  };

  return (
    <div className="App">

      <div>
        <h1>React Course Project</h1>
        <button onClick={toggleHandler}>Toggle</button>
        <UserInput change={onChangeHanlder} />
        {userNameState.showPerson === false ?

          <div>
            <UserOutput username={userNameState.usernames[0].username} style={style} />
            <UserOutput username={userNameState.usernames[1].username} style={style} change={onChangeHanlder} />
            <UserOutput username={userNameState.usernames[2].username} style={style} />

          </div>
          : null}

      </div>



    </div>
  );
}

export default App;
