import React, { useState } from 'react';

import './App.css';
import UserInput from "./components/UserInput";
import UserOutput from "./components/UserOutput";



function App(props) {
  const [userNameState, setUserNameState] = useState({
    usernames: [
      { username: "Max" },
      { username: "Tony" },
      { username: "Barbonie" },
    ],
    showPerson: true,
  });

  const onChangeHanlder = (e) => {
    setUserNameState({
      usernames: [
        { username: "Max" },
        { username: e.target.value },
        { username: "Barbonie"},
      ]
    })
    console.log(userNameState)
  }
  const style = {
    backgroundColor: 'gray',
    border: '1px solid black',
    padding: '8px',
    cursor: 'pointer',
  };

  const toggleHandler = (e) => {
    const doesShow = userNameState.usernames.showPerson;
    setUserNameState({ showPerson: !doesShow });
  }

  return (
    <div className="App">

      <div>
        <h1>Maximillian Udemy Course Project</h1>
        <button onClick={toggleHandler}>Toggle</button>
        <UserInput change={onChangeHanlder} />
        {
          userNameState.showPerson === true ?
            <div>
              <UserOutput username={userNameState.usernames[0].username} style={style} />
              <UserOutput change={onChangeHanlder} username={userNameState.usernames[1].username} style={style} />
              <UserOutput username={userNameState.usernames[2].username} style={style} />

            </div> : null
        }

      </div>



    </div>
  );
}

export default App;
