    usernames: [
      {username: "Max"},
      {username: "Tony"},
      {username: "Barbonie"},
    ],
    showPerson: false,
  });

  const onChangeHanlder = (e) => {
    const temp = userNameState.showPerson;
    setUserNameState({
      usernames: [
        {username: "Max"},
        {username: e.target.value},
        {username: "Barbonie"},
      ],
      showPerson: temp,
    })
    console.log("state", userNameState.usernames)
