    const doesShow = userNameState.showPerson;
    const usernames = [...userNameState.usernames];
    setUserNameState(
      {
        usernames: usernames,
        showPerson: !doesShow
      });
    console.log("toggle", userNameState.showPerson)
  };

  let people = null;
  if (userNameState.showPerson === false) {
    people =
      <div>
        {userNameState.usernames.map(index=>{
