import React, {useState} from 'react';

import './App.css';
import UserInput from "./components/UserInput";
import UserOutput from "./components/UserOutput";



function App(props) {
  const [userNameState, setUserNameState]= useState({
    usernames:[
    {username:"Max"},
    {username:"Tony"},
    {username:"Barbonie"}
  ]});

  const onChangeHanlder =(e)=>{
      setUserNameState({
          ...userNameState,
          [e.target.name]:e.target.value
      })
      console.log(userNameState)
  }

  return (
    <div className="App">
      <UserInput change={onChangeHanlder}/>
      <UserOutput username={userNameState.usernames[0].username}/>
      <UserOutput username={userNameState.usernames[1].username}/>
      <UserOutput username={userNameState.usernames[2].username}/>
    </div>
  );
}

export default App;
