import React, { useState } from 'react';

import './App.css';
import UserInput from "./components/UserInput";
import UserOutput from "./components/UserOutput";



function App(props) {
  const [userNameState, setUserNameState] = useState({
    usernames: [
      { username: "Max" },
      { username: "Tony" },
      { username: "Barbonie" }
    ]
  });

  const onChangeHanlder = (e) => {
    setUserNameState({
      usernames: [
        { username: "Max" },
        { username: e.target.value },
        { username: "Barbonie" },
        {showPerson: false},
      ]
    })
    console.log(userNameState)
  }
  const style = {
    backgroundColor: 'gray',
    border: '1px solid black',
    padding: '8px',
    cursor: 'pointer',
  };

  const hideHandler = (e) => {
    const doesShow= userNameState.showPerson;
    setUserNameState({showPerson: !doesShow});
  }

  return (
    <div className="App">
      {
      userNameState.showPerson === false ? 
      <div>
        <h1>This is the first assignment!</h1>
        <UserInput change={onChangeHanlder}/>
        <div>

        </div>
        <UserOutput username={userNameState.usernames[0].username} style={style} />
        <UserOutput onClick={hideHandler} username={userNameState.usernames[1].username} style={style} />
        <UserOutput username={userNameState.usernames[2].username} style={style} />
      </div> 
      : null
      }


    </div>
  );
}

export default App;
