import React, {useState} from 'react'


export const UserInput = (props) => {

    const [userNameState, setUserNameState]= new useState

    const onChangeHanlder =(e)=>{
        
        setUserNameState({
            ...userNameState,
            [e.target.name]:e.target.value
        })
    
    }
    console.log(userNameState)
    
        

    return (
        


        


        <div>
            
            <p>UserInput here: <input name="name" onChange={onChangeHanlder} type="text"></input></p>

            
        </div>
    )
}

export default UserInput;
